CREATE TABLE [datasets] (
  [id] int PRIMARY KEY IDENTITY(1, 1),
  [history] int,
  [cityID] int,
  [descriptions] nvarchar(255),
  [link] nvarchar(255),
  [name] nvarchar(255)
)
GO

CREATE TABLE [cities] (
  [id] int PRIMARY KEY,
  [name] nvarchar(255)
)
GO

ALTER TABLE [datasets] ADD FOREIGN KEY ([link]) REFERENCES [cities] ([id])
